import Button from './Button.js';
import twittericon from './images/twittericon.png';
import { useState, useEffect, useRef } from 'react';


function QuoteBox() {
	const [data, setData] = useState('');
	const [count, setCount] = useState(0);
	let url = 'https://swapi.dev/api/planets/'
	// let url = 'http://jsonplaceholder.typicode.com/albums/'
	// let url = 'https://jsonplaceholder.typicode.com/users'
	// const url = 'https://api.adviceslip.com/advice';

	useEffect(() => {
		const fetchIt = async () => {
		const res = await fetch(url, {cache: 'no-cache'});
		var data = await res.json()

		setData(data)
		}

		fetchIt()
	}, [])


	function handleClick() {
		setCount(prevcount => prevcount + 1)
		// console.log(count)
	}

	if (!data) {
		return <div>LOADING</div>
	}

	const ressies = data.results
	var item = ressies[Math.floor(Math.random() * ressies.length)];
	console.log('con', item)

	// console.log(data)

	// useEffect(() => {
	// 	fetch(url)
	// 		.then(res => res.json())
	// 		.then(json => console.log(json))
	// 		.then(datz => setData(datz))
	// 		.catch(err => console.log(err));
	// }, [])

	// console.log(data)



	return(
		<div id='quote-box'>
			<h2 id='text'>{item.name}</h2>
			{/*{item.map(thing => {
				return(
					<h2>{thing.name}</h2>
				)
			})}*/}
			{/*<h2 id='text'>"{item.name}"</h2>*/}
			{/*{data.map(t => {
				return(
					<h2 key={t.id}>{t.name}</h2>
				)

			})}*/}

			{/*<h2>{data}</h2>*/}

			<h5 id='author'>-Author</h5>

			<div id='new-quote'>
				{/*<Button OnClick={fetchData} text='New Quote'/>*/}

				<button onClick={handleClick}>Click here</button>
					<h5>{count}</h5>
			</div>

			<a href='#1' id='tweet-quote'>
				<img id='twitter-icon' alt='twitter icon' src={twittericon} />
			</a>
		</div>
	);
}

export default QuoteBox;
