export default function WelcomeBox() {
	return(
		<div className='welcome-box'>
			<button className='close-welcome'>X</button>
			<p className='welcome-tag'>
				Welcome to the Star Wars Planet Explorer!
			</p>
			<p className='description'>
				By navigating using the menu below, you can visit and learn all about your favorite planets! If you consider a parsec a unit of time, we'll get you there in less than 12 of 'em. If you consider a parsec a measure of distance...then you'd better hang on!
			</p>
		</div>
	)
}

