import Button from './Button.js';
// import twittericon from './images/twittericon.png';
// import nab from './Naboo.jpg';
// import './Naboo.jpg';
import { useState, useEffect} from 'react';


function PlanetBox() {
	const [data, setData] = useState('');
	const [toggle, setToggle] = useState(false)
	let url = 'https://swapi.dev/api/planets/'

	useEffect(() => {
		const fetchIt = async () => {
		const res = await fetch(url);
		var data = await res.json()

		setData(data)
		}

		fetchIt()
		console.log('re-rendered')
	}, [])


	function handleClick() {
		setToggle(prevState => !prevState)
	}


	if (!data) {
		return <div className='loading'>loading...</div>
	}

	const ressies = data.results
	console.log(ressies)
	var item = ressies[Math.floor(Math.random() * ressies.length)];
	console.log(item)

	var keys = []
	var values = []

	Object.keys(item).slice(0, -5).map((key, index) => {
		keys.push(key);
		values.push(item[key])
	})

	for (let i = 0; i < keys.length; i++) {
		keys[i] = keys[i].replace(/_/g, ' ');
	}

	function imageImport(r) {
		let images = {}
		r.keys().forEach((item, index) => {
			images[item.replace('./', '')] = r(item);

		});
		return images
	}

	const allImages = imageImport(require.context('./images/', false))
	console.log('all images', allImages)
	console.log('all images22222', allImages[item.name]+'webp')

	const imageArr = Object.keys(allImages)

	const namedImageArr = imageArr.map((name) => {
		return (name.split('.')[0])
	}) 

	// console.log('named correctly:', namedImageArr)
	// console.log('come on:', namedImageArr[item.name])

	// var itemImage = imageArr[0].split('.')[0]

	// console.log('item name', item.name)
	// console.log('keyssssssss', Object.keys(allImages))
	// console.log('keyssssssss', Object.keys(allImages))
	// console.log('keyssssssss', Object.keys(allImages).split('.')[0])
	// console.log('did it work?', Object.keys(allImages)[item.name])

	// display a semitransparent background image of the planet described
	// assign some slight and themed bg color swap depending on planet
	// fix all the unstructured css names
	// allow visit to any specific planet with the little bottom dots?


	return(
		<div id='planet-box'>
			{/*<img className='planet-bg' src={nab} alt={nab}/>*/}
		<img className='planet-bg' src={item.name} alt={item.name}/>
			{/*<h1 id='planet-title'>{item.name === 'Yavin IV' ? 'Yavin 4' : item.name}</h1>*/}
			<h1 id='planet-title'>{item.name}</h1>

			<div className='fetched'>
				<div className='fetched-keys'>
					{keys.map(i => {
						return(
							<li className='fetched-item' key={i}>{i}:</li>
						)

						})

					}
				</div>
				<div className='fetched-values'>
					{values.map(i => {
						return(
							<li className='fetched-item' key={i}>{i}</li>
						)

						})

					}
				</div>
			</div>

			<div id='new-planet'>
				<Button handleClick={handleClick} id='new-planet-text' text='Visit New Planet'/>
			</div>

			{/*<a href='#1' id='tweet-quote'>
				<img id='twitter-icon' alt='twitter icon' src={twittericon} />
			</a>*/}
		</div>
	);
}

export default PlanetBox;
