import Button from './Button.js';
import twittericon from './images/twittericon.png';
import { useState, useEffect, useRef } from 'react';


function QuoteBox() {
	const [data, setData] = useState('');
	const [toggle, setToggle] = useState(false)
	let url = 'https://swapi.dev/api/planets/'
	// let url = 'http://jsonplaceholder.typicode.com/albums/'
	// let url = 'https://jsonplaceholder.typicode.com/users'
	// const url = 'https://api.adviceslip.com/advice';

	useEffect(() => {
		const fetchIt = async () => {
		const res = await fetch(url);
		var data = await res.json()

		setData(data)
		}

		fetchIt()
		console.log('re-rendered')
	}, [])


	function handleClick() {
		setToggle(prevState => !prevState)
	}


	if (!data) {
		return <div>LOADING</div>
	}

	const ressies = data.results
	var item = ressies[Math.floor(Math.random() * ressies.length)];
	console.log(item)

	// console.log(
	// 	Object.keys(item).map(dat => {
	// 			console.log('dat', dat)
	// 		})
	// 	)

	// var what = Object.keys(item)
	// console.log(what)
	// console.log('this', what.map(d => d+':'))

	return(
		<div id='quote-box'>
			<h2 id='text'>{item.name}</h2>

			<h3>
				{Object.keys(item).forEach((d) => {
					return(
						<li key={d.name}>{d.name}</li>
					)
					})}
			</h3>

			<h5 id='author'>-Author</h5>
			<div id='new-quote'>
				<Button handleClick={handleClick} text='New Quote'/>
			</div>

			<a href='#1' id='tweet-quote'>
				<img id='twitter-icon' alt='twitter icon' src={twittericon} />
			</a>
		</div>
	);
}

export default QuoteBox;
